import { Organization as DbOrg, Prisma, PrismaClient } from '@prisma/client';

import { Category, MachineError, OnboardingError } from '../errors';
import { Organization as Org } from '../protos/api_pb';
import { AdminService } from '../services/adminService';
import { Entity } from '../types';
import { ExternalUuid } from '../utils';

import { Program } from './program';
import { Role } from './role';

const prisma = new PrismaClient();

export class Organization {
  public static async insertOne(
    organization: Prisma.OrganizationCreateInput
  ): Promise<void> {
    try {
      await prisma.organization.create({
        data: organization,
      });
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.WRITE,
        msg,
        Entity.ORGANIZATION,
        Category.POSTGRES,
        { entityId: organization.externalUuid, operation: 'INSERT ONE' }
      );
    }
  }

  public static async findOne(id: ExternalUuid): Promise<DbOrg> {
    try {
      const org = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
      });
      if (!org) throw new Error(`Organization: ${id} not found`);
      return org;
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Entity.ORGANIZATION,
        Category.POSTGRES,
        { entityId: id, operation: 'FIND ONE' }
      );
    }
  }

  public static async isValid(id: ExternalUuid): Promise<void> {
    try {
      const count = await prisma.organization.count({
        where: {
          externalUuid: id,
        },
      });
      if (count === 1) return;
      throw new Error(`Organization: ${id} is not valid`);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.VALIDATION,
        msg,
        Entity.ORGANIZATION,
        Category.POSTGRES,
        { entityId: id, operation: 'IS VALID' }
      );
    }
  }

  public static async getPrograms(id: ExternalUuid): Promise<string[]> {
    try {
      const programs = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
        select: {
          programs: {
            select: {
              klUuid: true,
            },
          },
        },
      });
      if (!programs) throw new Error(`Organization: ${id} not found`);
      return programs.programs.map((p) => p.klUuid);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Entity.ORGANIZATION,
        Category.POSTGRES,
        { entityId: id, operation: 'GET PROGRAMS' }
      );
    }
  }

  public static async programsAreValid(
    orgId: ExternalUuid,
    programs: ExternalUuid[]
  ): Promise<void> {
    const validPrograms = new Set(await Organization.getPrograms(orgId));
    const invalidPrograms = [];
    for (const program of programs) {
      if (!validPrograms.has(program)) invalidPrograms.push(program);
    }
    if (invalidPrograms.length > 0)
      throw new OnboardingError(
        MachineError.VALIDATION,
        `Programs: ${invalidPrograms.join(
          ', '
        )} are invalid when comparing to the parent Organization ${orgId}`,
        Entity.PROGRAM,
        Category.REQUEST,
        { entityIds: programs, operation: 'PROGRAMS ARE VALID' }
      );
  }

  public static async getRoles(id: ExternalUuid): Promise<ExternalUuid[]> {
    try {
      const roles = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
        select: {
          roles: {
            select: {
              klUuid: true,
            },
          },
        },
      });
      if (!roles) throw new Error(`Organization: ${id} not found`);
      return roles.roles.map((r) => r.klUuid);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Entity.ORGANIZATION,
        Category.POSTGRES,
        { entityId: id, operation: 'GET ROLES' }
      );
    }
  }

  public static async rolesAreValid(
    orgId: ExternalUuid,
    roles: ExternalUuid[]
  ): Promise<void> {
    const validRoles = new Set(await Organization.getRoles(orgId));
    const invalidRoles = [];
    for (const role of roles) {
      if (!validRoles.has(role)) invalidRoles.push(role);
    }
    if (invalidRoles.length > 0)
      throw new OnboardingError(
        MachineError.VALIDATION,
        `Roles: ${invalidRoles.join(', ')} are invalid`,
        Entity.ROLE,
        Category.REQUEST,
        { entityIds: roles, operation: 'ROLES ARE VALID' }
      );
  }

  public static async initializeOrganization(org: Org): Promise<void> {
    const admin = await AdminService.getInstance();
    const name = org.getName();
    const externalUuid = org.getExternalUuid();
    const klUuid = await admin.getOrganization(name);
    const systemPrograms = await admin.getSystemPrograms();
    const systemRoles = await admin.getSystemRoles();
    const customPrograms = await admin.getOrganizationPrograms(klUuid);
    const customRoles = await admin.getOrganizationRoles(klUuid);
    const programUuids = systemPrograms.concat(customPrograms);
    const roleUuids = systemRoles.concat(customRoles);

    await Organization.insertOne({
      externalUuid,
      klUuid,
      name,
    });
    await Program.insertMany(programUuids, externalUuid);
    await Role.insertMany(roleUuids, externalUuid);
  }
}

import { log } from '..';

import { Entity } from './types';

export enum Category {
  REQUEST = 'REQUEST',
  APP = 'APPLICATION',
  REDIS = 'REDIS',
  POSTGRES = 'POSTGRES',
  ADMIN_SERVICE = 'ADMIN SERVICE',
  C1_API = 'C1 API',
  UNKNOWN = 'UNKNOWN',
  PROTOBUF = 'PROTOBUF',
}

export enum MachineError {
  VALIDATION = 'Validation',
  UNREACHABLE_CODE = 'Unreachable',
  READ = 'Read Operation',
  WRITE = 'Write Operation',
  STREAM = 'Data Stream',
  APP_CONFIG = 'Application Configuration',
  SERDE = 'Serde',
  NETWORK = 'Network',
  NOT_FOUND = 'Not Found',
}

type LogLevel = 'error' | 'warn' | 'info' | 'debug' | 'trace';

export class Errors {
  constructor(public errors: OnboardingError[]) {}
}

export class OnboardingError {
  private hasBeenLogged = false;
  constructor(
    public readonly error: MachineError,
    public readonly msg: string,
    public readonly entity: Entity,
    public readonly category: Category,
    public readonly properties = {},
    public readonly details: string[] = [],
    public readonly logLevel: LogLevel = 'error',
    public readonly logOnCreation = true
  ) {
    if (logOnCreation) this.log();
  }

  public log(): void {
    if (this.hasBeenLogged) return;
    const props: Record<string, string | string[] | number | boolean> = {
      ...this.properties,
      category: this.category,
      error: this.error,
      entity: this.entity.toString(),
    };
    if (this.details.length > 0) props['details'] = this.details;
    switch (this.logLevel) {
      case 'warn':
        log.warn(props, this.msg);
        break;
      case 'info':
        log.info(props, this.msg);
        break;
      case 'trace':
        log.trace(props, this.msg);
        break;
      case 'debug':
        log.debug(props, this.msg);
        break;
      case 'error':
      default:
        log.error(props, this.msg);
        break;
    }
    this.hasBeenLogged = true;
  }
}

export const UNREACHABLE = () =>
  new OnboardingError(
    MachineError.UNREACHABLE_CODE,
    'This error should not be reachable',
    Entity.UNKNOWN,
    Category.APP
  );

export const ENVIRONMENT_VARIABLE_ERROR = (envVar: string) =>
  new OnboardingError(
    MachineError.APP_CONFIG,
    `Environment Variable ${envVar} set incorrectly`,
    Entity.UNKNOWN,
    Category.APP
  );

import { proto } from '../..';
import { Category, MachineError, OnboardingError } from '../errors';

export enum Entity {
  ORGANIZATION = 'Organization',
  SCHOOL = 'School',
  CLASS = 'Class',
  USER = 'User',
  ROLE = 'Role',
  PROGRAM = 'Program',
  UNKNOWN = 'Unknown',
}

export function entityToProtobuf(e: Entity): proto.Entity {
  switch (e) {
    case Entity.ORGANIZATION:
      return proto.Entity.ORGANIZATION;
    case Entity.SCHOOL:
      return proto.Entity.SCHOOL;
    case Entity.CLASS:
      return proto.Entity.CLASS;
    case Entity.USER:
      return proto.Entity.USER;
    case Entity.PROGRAM:
      return proto.Entity.PROGRAM;
    case Entity.ROLE:
      return proto.Entity.ROLE;
    default:
      throw new OnboardingError(
        MachineError.APP_CONFIG,
        'Unable to map app Entity to protobuf Entity',
        Entity.UNKNOWN,
        Category.APP
      );
  }
}

import {
  Category,
  MachineError,
  OnboardingError,
  UNREACHABLE,
} from '../errors';
import { OnboardingRequest } from '../protos/api_pb';
import { Event } from '../protos/stream_pb';

import { Entity } from '.';

export class Message {
  private constructor(
    public readonly data: OnboardingRequest,
    private attempts: number = 0,
    public readonly redisMessageId?: string
  ) {}

  get entity(): Entity {
    switch (this.data.getEntityCase()) {
      case OnboardingRequest.EntityCase.ORGANIZATION:
        return Entity.ORGANIZATION;
      case OnboardingRequest.EntityCase.SCHOOL:
        return Entity.SCHOOL;
      case OnboardingRequest.EntityCase.CLASS:
        return Entity.CLASS;
      case OnboardingRequest.EntityCase.USER:
        return Entity.USER;
      default:
        // Unreachable
        throw UNREACHABLE();
    }
  }

  public static deserialize(data: string, redisMessageId?: string): Message {
    const d = data.split(',').map((n) => Number(n));
    const bytes = new Uint8Array(d);
    const msg = Event.deserializeBinary(bytes);
    const message = msg.getData();
    if (!message)
      throw new OnboardingError(
        MachineError.SERDE,
        'Failed to deserialize binary message from stream',
        Entity.UNKNOWN,
        Category.PROTOBUF
      );
    return new Message(message, msg.getRetries(), redisMessageId);
  }

  public static fromOnboardingRequest(r: OnboardingRequest): Message {
    return new Message(r);
  }

  public serialize(): Uint8Array {
    try {
      const event = this.toStreamEvent();
      const bytes = event.serializeBinary();
      return bytes;
    } catch (error) {
      const msg =
        error instanceof Error
          ? error.message
          : 'Failed to serialize protobuf message';
      throw new OnboardingError(
        MachineError.SERDE,
        msg,
        Entity.UNKNOWN,
        Category.PROTOBUF
      );
    }
  }

  private toStreamEvent(): Event {
    const event = new Event();
    event.setData(this.data);
    event.setRetries(this.attempts);
    return event;
  }

  get processingAttempts(): number {
    return this.attempts;
  }
}

import {
  ApolloClient,
  DocumentNode,
  from,
  HttpLink,
  InMemoryCache,
  NormalizedCacheObject,
  TypedDocumentNode,
} from '@apollo/client/core';
import { onError } from '@apollo/client/link/error';
import { RetryLink } from '@apollo/client/link/retry';
import fetch from 'cross-fetch';

import {
  Category,
  ENVIRONMENT_VARIABLE_ERROR,
  MachineError,
  OnboardingError,
} from '../../errors';
