export * from './lib/entities';
export * from './lib/errors';
export * as proto from './lib/protos';
export * from './lib/redis';
export * from './lib/validation';
export * from './lib/utils';
export * from './lib/types';
export * from './lib/services';
export { log } from './lib/utils';
export { Prisma, PrismaClient } from '@prisma/client';

export { Organization } from './organization';
export { School } from './school';
export { Class } from './class';
export { User } from './user';
export { Program } from './program';
export { Role } from './role';

import { Organization as DbOrg, Prisma, PrismaClient } from '@prisma/client';
import { Logger } from 'pino';

import {
  Category,
  MachineError,
  OnboardingError,
  POSTGRES_GET_KIDSLOOP_ID_QUERY,
  POSTGRES_IS_VALID_QUERY,
} from '../errors';
import { Organization as Org } from '../protos/api_pb';
import { AdminService } from '../services/adminService';
import { Entity } from '../types';
import { ExternalUuid, Uuid } from '../utils';

import { Program } from './program';
import { Role } from './role';

const prisma = new PrismaClient();

export class Organization {
  public static entity = Entity.ORGANIZATION;

  public static async insertOne(
    organization: Prisma.OrganizationCreateInput,
    log: Logger
  ): Promise<void> {
    try {
      await prisma.organization.create({
        data: organization,
      });
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.WRITE,
        msg,
        Category.POSTGRES,
        log,
        [],
        { entityId: organization.externalUuid, operation: 'INSERT ONE' }
      );
    }
  }

  public static async findOne(id: ExternalUuid, log: Logger): Promise<DbOrg> {
    try {
      const org = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
      });
      if (!org) throw new Error(`Organization: ${id} not found`);
      return org;
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Category.POSTGRES,
        log,
        [],
        { entityId: id, operation: 'FIND ONE' }
      );
    }
  }

  public static async isValid(id: ExternalUuid, log: Logger): Promise<boolean> {
    try {
      const school = await prisma.organization.findFirst({
        where: {
          externalUuid: id,
        },
        select: {
          externalUuid: true,
        },
      });
      if (school) return true;
      throw new Error(`${this.entity}: ${id} is not valid`);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw POSTGRES_IS_VALID_QUERY(id, this.entity, msg, log);
    }
  }

  public static async getId(id: ExternalUuid, log: Logger): Promise<Uuid> {
    try {
      const klUuid = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
        select: {
          klUuid: true,
        },
      });
      if (!klUuid) throw new Error(`${this.entity}: ${id} is not valid`);
      if (klUuid && klUuid.klUuid) return klUuid.klUuid;
      throw new Error(`Unable to find KidsLoop ID for ${this.entity}: ${id}`);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw POSTGRES_GET_KIDSLOOP_ID_QUERY(id, this.entity, msg, log);
    }
  }

  public static async getPrograms(
    id: ExternalUuid,
    log: Logger
  ): Promise<string[]> {
    try {
      const programs = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
        select: {
          programs: {
            select: {
              klUuid: true,
            },
          },
        },
      });
      if (!programs) throw new Error(`Organization: ${id} not found`);
      return programs.programs.map((p) => p.klUuid);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Category.POSTGRES,
        log,
        [],
        { entityId: id, operation: 'GET PROGRAMS' }
      );
    }
  }

  public static async programsAreValid(
    orgId: ExternalUuid,
    programs: ExternalUuid[],
    log: Logger
  ): Promise<void> {
    const validPrograms = new Set(await Organization.getPrograms(orgId, log));
    const invalidPrograms = [];
    for (const program of programs) {
      if (!validPrograms.has(program)) invalidPrograms.push(program);
    }
    if (invalidPrograms.length > 0)
      throw new OnboardingError(
        MachineError.VALIDATION,
        `Programs: ${invalidPrograms.join(
          ', '
        )} are invalid when comparing to the parent Organization ${orgId}`,
        Category.REQUEST,
        log,
        [],
        { entityIds: programs, operation: 'PROGRAMS ARE VALID' }
      );
  }

  public static async getRoles(
    id: ExternalUuid,
    log: Logger
  ): Promise<ExternalUuid[]> {
    try {
      const roles = await prisma.organization.findUnique({
        where: {
          externalUuid: id,
        },
        select: {
          roles: {
            select: {
              klUuid: true,
            },
          },
        },
      });
      if (!roles) throw new Error(`Organization: ${id} not found`);
      return roles.roles.map((r) => r.klUuid);
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.READ,
        msg,
        Category.POSTGRES,
        log,
        [],
        { entityId: id, operation: 'GET ROLES' }
      );
    }
  }

  public static async initializeOrganization(
    org: Org.AsObject,
    log: Logger
  ): Promise<void> {
    try {
      const admin = await AdminService.getInstance();
      const { name, externalUuid } = org;
      const klUuid = await admin.getOrganization(name, log);
      const systemPrograms = await admin.getSystemPrograms(log);
      const systemRoles = await admin.getSystemRoles(log);
      const customPrograms = await admin.getOrganizationPrograms(klUuid, log);
      const customRoles = await admin.getOrganizationRoles(klUuid, log);
      const programUuids = systemPrograms.concat(customPrograms);
      const roleUuids = systemRoles.concat(customRoles);

      await Organization.insertOne(
        {
          externalUuid,
          klUuid,
          name,
        },
        log
      );
      await Program.insertMany(programUuids, externalUuid, log);
      await Role.insertMany(roleUuids, externalUuid, log);
    } catch (error) {
      if (error instanceof OnboardingError) throw error;
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.VALIDATION,
        msg,
        Category.REQUEST,
        log
      );
    }
  }
}

import { Logger } from 'pino';

import { Entity, ExternalUuid, log } from '..';

export const BASE_PATH = Object.freeze(['$']);

export enum Category {
  REQUEST = 'REQUEST',
  APP = 'APPLICATION',
  REDIS = 'REDIS',
  POSTGRES = 'POSTGRES',
  ADMIN_SERVICE = 'ADMIN SERVICE',
  UNKNOWN = 'UNKNOWN',
  PROTOBUF = 'PROTOBUF',
}

export enum MachineError {
  VALIDATION = 'Validation',
  UNREACHABLE_CODE = 'Unreachable',
  READ = 'Read Operation',
  WRITE = 'Write Operation',
  STREAM = 'Data Stream',
  APP_CONFIG = 'Application Configuration',
  SERDE = 'Serde',
  NETWORK = 'Network',
  NOT_FOUND = 'Not Found',
  REQUEST = 'Bad Request',
}

type LogLevel = 'error' | 'warn' | 'info' | 'debug' | 'trace';

export type Props = Record<string, string | string[] | number | boolean>;

export class Errors {
  constructor(public errors: OnboardingError[]) {}
}

export class OnboardingError {
  private hasBeenLogged = false;
  constructor(
    public readonly error: MachineError,
    public readonly msg: string,
    public readonly category: Category,
    public logger: Logger = log,
    public readonly path: string[] = [],
    public readonly properties = {},
    public readonly details: string[] = [],
    public readonly logLevel: LogLevel = 'error',
    public readonly logOnCreation = true
  ) {
    if (logOnCreation) this.log();
  }

  public log(): void {
    if (this.hasBeenLogged) return;
    const props: Props = {
      ...this.properties,
      category: this.category,
      error: this.error,
      path: this.path.join('.'),
    };
    if (this.details.length > 0) props['details'] = this.details;
    switch (this.logLevel) {
      case 'warn':
        this.logger.warn(props, this.msg);
        break;
      case 'info':
        this.logger.info(props, this.msg);
        break;
      case 'trace':
        this.logger.trace(props, this.msg);
        break;
      case 'debug':
        this.logger.debug(props, this.msg);
        break;
      case 'error':
      default:
        this.logger.error(props, this.msg);
        break;
    }
    this.hasBeenLogged = true;
  }
}

export const UNREACHABLE = () =>
  new OnboardingError(
    MachineError.UNREACHABLE_CODE,
    'This error should not be reachable',
    Category.APP
  );

export const ENVIRONMENT_VARIABLE_ERROR = (envVar: string) =>
  new OnboardingError(
    MachineError.APP_CONFIG,
    `Environment Variable ${envVar} set incorrectly`,
    Category.APP
  );

export const BAD_REQUEST = (
  msg: string,
  path: string[],
  log: Logger,
  props: Props = {}
) =>
  new OnboardingError(
    MachineError.REQUEST,
    msg,
    Category.REQUEST,
    log,
    path,
    props
  );

export const INVALID_ENTITY = (id: ExternalUuid, entity: Entity, log: Logger) =>
  new OnboardingError(
    MachineError.VALIDATION,
    `Entity ${entity} has no valid item for id ${id}`,
    Category.REQUEST,
    log
  );

export const POSTGRES_IS_VALID_QUERY = (
  id: ExternalUuid | ExternalUuid[],
  targetEntity: Entity,
  msg: string,
  log: Logger
) => {
  if (Array.isArray(id)) {
    return new OnboardingError(
      MachineError.VALIDATION,
      msg,
      Category.POSTGRES,
      log,
      [],
      { operation: 'IS VALID', targetEntity, targetEntityIds: id.join(', ') }
    );
  }

  return new OnboardingError(
    MachineError.VALIDATION,
    msg,
    Category.POSTGRES,
    log,
    [],
    { operation: 'IS VALID', targetEntity, targetEntityId: id }
  );
}

export const POSTGRES_GET_KIDSLOOP_ID_QUERY = (
  id: ExternalUuid | ExternalUuid[],
  targetEntity: Entity,
  msg: string,
  log: Logger
) => {
  if (Array.isArray(id)) {
    return new OnboardingError(MachineError.READ, msg, Category.POSTGRES, log, [], {
      operation: 'GET KIDSLOOP IDS',
      targetEntity,
      targetEntityIds: id.join(', '),
    });
  }
  return new OnboardingError(MachineError.READ, msg, Category.POSTGRES, log, [], {
    operation: 'GET KIDSLOOP ID',
    targetEntity,
    targetEntityId: id,
  });
}


export function tryGetMember<T>(
  e: T | undefined,
  log: Logger,
  path: string[] = []
): T {
  if (e === undefined)
    throw new OnboardingError(
      MachineError.VALIDATION,
      `Expected to find data however it was undefined`,
      Category.REQUEST,
      log,
      path
    );
  return e;
}

import {
  ApolloClient,
  DocumentNode,
  from,
  HttpLink,
  InMemoryCache,
  NormalizedCacheObject,
  TypedDocumentNode,
} from '@apollo/client/core';
import { onError } from '@apollo/client/link/error';
import { RetryLink } from '@apollo/client/link/retry';
import fetch from 'cross-fetch';
import { Logger } from 'pino';

import {
  Category,
  ENVIRONMENT_VARIABLE_ERROR,
  MachineError,
  OnboardingError,
} from '../../errors';
import { Entity } from '../../types';
import { log as baseLogger, Uuid } from '../../utils';

import { GET_ORGANIZATION } from './organization';
import { GET_PROGRAMS_BY_ORGANIZATION, GET_SYSTEM_PROGRAMS } from './programs';
import { GET_ORGANIZATION_ROLES, GET_SYSTEM_ROLES } from './roles';

type SupportedConnections =
  | 'programsConnection'
  | 'rolesConnection'
  | 'organizationConnection';

export type IdNameMapper = {
  id: Uuid;
  name: string;
};

const idNameTransformer = ({ id, name }: IdNameMapper): IdNameMapper => ({
  id,
  name,
});

export class AdminService {
  private static _instance: AdminService;
  private context: { headers: { authorization: string } };

  private constructor(
    private _client: ApolloClient<NormalizedCacheObject>,
    jwt: string
  ) {
    this.context = { headers: { authorization: jwt } };
  }

  public static async getInstance() {
    if (this._instance) return this._instance;

    const jwt = process.env.ADMIN_SERVICE_JWT;
    if (!jwt || jwt.length === 0) {
      throw ENVIRONMENT_VARIABLE_ERROR('ADMIN_SERVICE_JWT');
    }

    const httpLink = new HttpLink({
      uri: process.env.ADMIN_SERVICE_URL,
      fetch,
    });
    /**
     * Only retry network errors
     *
     * Reference: https://www.apollographql.com/docs/react/api/link/apollo-link-retry/
     */
    const retryLink = new RetryLink({
      delay: {
        initial: 300,
        max: Infinity,
        jitter: true,
      },
      attempts: {
        max: 5,
        retryIf: (error, _operation) => !!error,
      },
    });
    const errorLink = onError(({ graphQLErrors, networkError, response }) => {
      /**
       * GraphQL errors, will not retry
       *
       * - Syntax errors (e.g., a query was malformed) - 4xx error
       * - Validation errors (e.g., a query included a schema field that doesn't exist) - 4xx error
       * - Resolver errors (e.g., an error occurred while attempting to populate a query field) - 2xx error
       *
       * Reference: https://www.apollographql.com/docs/react/data/error-handling
       */
      if (graphQLErrors)
        graphQLErrors.forEach(({ message, path }) =>
          baseLogger.error(`GraphQL query failed`, {
            error: message,
            api: 'admin',
            path,
          })
        );

      // 4xx/5xx errors
      if (networkError)
        baseLogger.error(`Network error while attempting a GraphQL call`, {
          error: networkError,
          api: 'admin',
        });

      if (response)
        baseLogger.error(`Received response but found an error`, {
          error: response,
          api: 'admin',
        });
    });

    try {
      const client = new ApolloClient({
        link: from([errorLink, retryLink, httpLink]),
        cache: new InMemoryCache(),
      });

      this._instance = new AdminService(client, jwt);
      baseLogger.info('Connected to KidsLoop admin service');
      return this._instance;
    } catch (error) {
      baseLogger.error(' Failed to connect KidsLoop admin service', {
        error,
      });
      throw error;
    }
  }

  get client(): ApolloClient<NormalizedCacheObject> {
    return this._client;
  }

  public async getSystemPrograms(log: Logger): Promise<IdNameMapper[]> {
    const results = await this.traversePaginatedQuery(
      GET_SYSTEM_PROGRAMS,
      idNameTransformer,
      'programsConnection',
      log
    );
    return results;
  }

  public async getOrganizationPrograms(
    orgId: Uuid,
    log: Logger
  ): Promise<IdNameMapper[]> {
    const results = await this.traversePaginatedQuery(
      GET_PROGRAMS_BY_ORGANIZATION,
      idNameTransformer,
      'programsConnection',
      log,
      { orgId }
    );
    return results;
  }

  public async getSystemRoles(log: Logger): Promise<IdNameMapper[]> {
    const results = await this.traversePaginatedQuery(
      GET_SYSTEM_ROLES,
      idNameTransformer,
      'rolesConnection',
      log
    );
    return results;
  }

  public async getOrganizationRoles(
    orgId: Uuid,
    log: Logger
  ): Promise<IdNameMapper[]> {
    const results = await this.traversePaginatedQuery(
      GET_ORGANIZATION_ROLES,
      idNameTransformer,
      'rolesConnection',
      log,
      { orgId }
    );
    return results;
  }

  public async getOrganization(orgName: string, log: Logger): Promise<Uuid> {
    const transformer = ({ id }: { id: string }) => id;
    const org = await this.traversePaginatedQuery(
      GET_ORGANIZATION,
      transformer,
      'organizationConnection',
      log,
      { orgName }
    );
    if (org.length > 1)
      throw new Error(
        `Unexpectedly found more than one organization with the name ${orgName}, unable to identify which one should be used`
      );
    if (org.length === 0) throw new Error(`Organization ${orgName} not found`);
    return org[0];
  }

  /**
   * A helper function to send a request to a paginated API and walk the
   * full length of the cursor, collating all the responses before returning
   * an array of items
   *
   * @param {string} query - The GraphQL query to be sent
   * @param {function} transformer - A function that will be called on each
   * node within the response to convert the response data into the desired format
   * @param {object} variables - Any variables that need to be provided to the
   * GraphQL query
   * @returns {T[]} An array of the transformed type
   */
  private async traversePaginatedQuery<T, U>(
    query: DocumentNode | TypedDocumentNode,
    transformer: (responseData: U) => T,
    connectionName: SupportedConnections,
    logger: Logger,
    variables?: Record<string, unknown>
  ): Promise<T[]> {
    let hasNextPage = true;
    let cursor = '';

    const result: T[] = [];
    while (hasNextPage) {
      /**
       * Don't need to handle errors here because:
       *
       * - 4xx/5xx were handled in `errorLink` when initializing `ApolloClient`
       * - 2xx errors won't exist in this case
       */
      const { data } = await this.client.query({
        query,
        variables: {
          count: 50,
          cursor,
          ...variables,
        },
        context: this.context,
      });

      const responseData = data[connectionName];
      if (!responseData || !responseData.pageInfo) {
        let entity = Entity.UNKNOWN;
        switch (connectionName) {
          case 'organizationConnection':
            entity = Entity.ORGANIZATION;
            break;
          case 'rolesConnection':
            entity = Entity.ROLE;
            break;
          case 'programsConnection':
            entity = Entity.PROGRAM;
            break;
          default:
            break;
        }

        throw new OnboardingError(
          MachineError.NOT_FOUND,
          'When trying to parse the paginated query, found no pages of data',
          Category.ADMIN_SERVICE,
          logger,
          [],
          { entity }
        );
      }
      hasNextPage = responseData.pageInfo.hasNextPage;
      cursor = responseData.pageInfo.endCursor;

      for (const { node } of responseData.edges) {
        result.push(transformer(node));
      }
    }
    return result;
  }
}

import { Logger } from 'pino';

import { proto } from '../..';
import { Category, MachineError, OnboardingError } from '../errors';

export enum Entity {
  ORGANIZATION = 'Organization',
  SCHOOL = 'School',
  CLASS = 'Class',
  USER = 'User',
  ROLE = 'Role',
  PROGRAM = 'Program',
  UNKNOWN = 'Unknown',
}

export function entityToProtobuf(e: Entity, log: Logger): proto.Entity {
  switch (e) {
    case Entity.ORGANIZATION:
      return proto.Entity.ORGANIZATION;
    case Entity.SCHOOL:
      return proto.Entity.SCHOOL;
    case Entity.CLASS:
      return proto.Entity.CLASS;
    case Entity.USER:
      return proto.Entity.USER;
    case Entity.PROGRAM:
      return proto.Entity.PROGRAM;
    case Entity.ROLE:
      return proto.Entity.ROLE;
    default:
      throw new OnboardingError(
        MachineError.APP_CONFIG,
        'Unable to map app Entity to protobuf Entity',
        Category.APP,
        log
      );
  }
}

export function protobufToEntity(e: proto.Entity, log: Logger): Entity {
  switch (e) {
    case proto.Entity.ORGANIZATION:
      return Entity.ORGANIZATION;
    case proto.Entity.SCHOOL:
      return Entity.SCHOOL;
    case proto.Entity.CLASS:
      return Entity.CLASS;
    case proto.Entity.USER:
      return Entity.USER;
    case proto.Entity.PROGRAM:
      return Entity.PROGRAM;
    case proto.Entity.ROLE:
      return Entity.ROLE;
    default:
      throw new OnboardingError(
        MachineError.APP_CONFIG,
        'Unable to map protobuf Entity to app Entity',
        Category.APP,
        log
      );
  }
}

import { Logger } from 'pino';

import { Category, MachineError, OnboardingError } from '../errors';
import { Job, OnboardingRequest } from '../protos';

export class Message {
  private constructor(
    public readonly job: Job,
    public readonly redisMessageId?: string
  ) {}

  get request(): OnboardingRequest | undefined {
    return this.job.getRequest();
  }

  public static deserialize(
    data: string,
    log: Logger,
    redisMessageId?: string
  ): Message {
    const d = data.split(',').map((n) => Number(n));
    const bytes = new Uint8Array(d);
    const msg = Job.deserializeBinary(bytes);
    if (!msg)
      throw new OnboardingError(
        MachineError.SERDE,
        'Failed to deserialize binary message from stream',
        Category.PROTOBUF,
        log
      );
    return new Message(msg, redisMessageId);
  }

  public static fromOnboardingRequest(r: OnboardingRequest): Message {
    const j = new Job();
    j.setRequest(r);
    return new Message(j);
  }

  public serialize(log: Logger): Uint8Array {
    try {
      const bytes = this.job.serializeBinary();
      return bytes;
    } catch (error) {
      const msg =
        error instanceof Error
          ? error.message
          : 'Failed to serialize protobuf message';
      throw new OnboardingError(
        MachineError.SERDE,
        msg,
        Category.PROTOBUF,
        log
      );
    }
  }
}

export * from './api_grpc_pb';
export * from './api_pb';
export * from './job_pb';

export { log } from './log';
export { processMessage } from './process';
export { Context } from './context';

export type ExternalUuid = string;
export type Uuid = string;

export async function sleep(ms: number): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

import pino from 'pino';

// eslint-disable-next-line @typescript-eslint/no-var-requires
const nrPino = require('@newrelic/pino-enricher');

export const log = pino({
  ...nrPino(),
  name: process.env.SERVICE_LABEL || 'cil',
  enabled: process.env.LOG_ENABLED === 'false' ? false : true,
  formatters: {
    level: (label) => ({
      level: label,
    }),
  },
});

import { Logger } from 'pino';

import { OnboardingError } from '../errors';
import {
  OnboardingRequest,
  // Error as PbError,
  Response,
  // ValidationError,
} from '../protos/api_pb';
import { RedisStream } from '../redis';
import { Message } from '../types';
import { actionToString } from '../types/action';
import { ValidationWrapper } from '../validation';

// @TODO - Need to actually implement error handling around this
// Currently we either throw an error or succeed (don't send error messages back)
export const processMessage = async (
  data: OnboardingRequest,
  log: Logger
): Promise<Response> => {
  const resp = new Response();
  resp.setRequestId(data.getRequestId());

  const logger: Logger = log;
  try {
    const wrapper = await ValidationWrapper.parseRequest(data, log);
    const logger = wrapper.logger.child({ currentOperation: 'PUBLISH' });
    logger.info(
      `Received request to perform action: ${actionToString(
        wrapper.data.getAction()
      )} ${wrapper.data.getPayloadCase().toString()}`
    );
    resp.setEntity(wrapper.entity);
    resp.setEntityId(wrapper.entityId);
  } catch (error) {
    // Need to set entity & entity ID on failure too
    resp.setSuccess(false);
    if (error instanceof OnboardingError) {
      // const e = new PbError();
      // const v = new ValidationError();
      // @TODO
    }
    return resp;
  }
  const msg = Message.fromOnboardingRequest(data);
  const stream = await RedisStream.getInstance(logger);
  await stream.publishMessage(msg, logger);
  resp.setSuccess(true);
  return resp;
};

import R from 'ioredis';
import { Logger } from 'pino';

import {
  Category,
  ENVIRONMENT_VARIABLE_ERROR,
  MachineError,
  OnboardingError,
} from '../errors';

export class RedisClient {
  private static _instance: RedisClient;

  private constructor(private redis: R.Redis | R.Cluster) {}

  public static async getInstance(log: Logger): Promise<RedisClient> {
    if (this._instance) return this._instance;
    log.info('Attempting to initialize Redis stream');
    checkRedisEnvVars(true);

    try {
      const client = await RedisClient.createClient();
      log.info('Succesfully initialized Redis client');
      this._instance = new RedisClient(client);
      return this._instance;
    } catch (error) {
      const msg = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(MachineError.NETWORK, msg, Category.REDIS, log);
    }
  }

  public static async createClient(): Promise<R.Redis | R.Cluster> {
    const redisMode = process.env.REDIS_MODE ?? `NODE`;
    const port = Number(process.env.REDIS_PORT) || undefined;
    const host = process.env.REDIS_HOST;
    const password = process.env.REDIS_PASSWORD;
    const lazyConnect = true;

    let redis: R.Redis | R.Cluster;
    if (redisMode === `CLUSTER`) {
      redis = new R.Cluster(
        [
          {
            port,
            host,
          },
        ],
        {
          lazyConnect,
          redisOptions: {
            password,
          },
        }
      );
    } else {
      redis = new R(port, host, {
        lazyConnect: true,
        password,
      });
    }
    await redis.connect();
    return redis;
  }

  get client(): R.Redis | R.Cluster {
    return this.redis;
  }
}

function checkRedisEnvVars(checkStream = true): void {
  let vars = ['REDIS_HOST', 'REDIS_PORT'];
  if (checkStream) {
    vars = vars.concat(['REDIS_CONSUMER_GROUP_NAME', 'REDIS_STREAM_NAME']);
  }
  for (const envVar of vars) {
    const env = process.env[envVar];
    if (!env || env.length === 0) {
      throw ENVIRONMENT_VARIABLE_ERROR(envVar);
    }
  }
}

import R from 'ioredis';
import { Logger } from 'pino';
import { v4 as uuidv4 } from 'uuid';

import { Category, MachineError, OnboardingError } from '../errors';
import { Message } from '../types';
import { Uuid } from '../utils';

import { RedisClient } from '.';

// 3 minutes
const STALE_MESSAGE = 60 * 1000 * 3;

export class RedisStream {
  private static _instance: RedisStream;
  private consumerId: Uuid;
  private counter = 0;
  private props = {};

  private constructor(
    private redis: R.Redis | R.Cluster,
    private stream: string,
    private consumerGroup: string
  ) {
    this.consumerId = uuidv4();
    this.props = {
      stream: this.stream,
      consumerGroup: this.consumerGroup,
      consumerId: this.consumerId,
    };
  }

  public static async getInstance(log: Logger): Promise<RedisStream> {
    if (this._instance) return this._instance;
    log.info('Attempting to initialize Redis stream');
    const client = (await RedisClient.getInstance(log)).client;
    const stream = process.env.REDIS_STREAM_NAME || '';
    const consumerGroup = process.env.REDIS_CONSUMER_GROUP_NAME || '';
    const props = {
      stream,
      consumerGroup,
    };
    try {
      const streamInfo = await client.xinfo('GROUPS', stream);
      for (const i of streamInfo) {
        if (!(i as string[]).includes(consumerGroup)) {
          await client.xgroup('CREATE', stream, consumerGroup, 0, 'MKSTREAM');
        }
      }
    } catch (e) {
      if (e instanceof Error && e.message === 'ERR no such key') {
        await client.xgroup('CREATE', stream, consumerGroup, 0, 'MKSTREAM');

        log.info(props, 'Succesfully initialized Redis stream');
        return new RedisStream(client, stream, consumerGroup);
      }
      const msg = e instanceof Error ? e.message : `${e}`;
      throw new OnboardingError(
        MachineError.STREAM,
        `Failed to initialize stream. ${msg}`,
        Category.REDIS,
        log,
        [],
        props
      );
    }
    log.info(props, 'Succesfully initialized Redis stream');
    this._instance = new RedisStream(client, stream, consumerGroup);
    return this._instance;
  }

  public async publishMessage(msg: Message, log: Logger): Promise<void> {
    try {
      const payload = msg.serialize(log);
      await this.redis.xadd(this.stream, '*', 'PROTO', payload.join(','));
    } catch (error) {
      const m = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.STREAM,
        `Failed to publish message to stream. ${m}`,
        Category.REDIS,
        log,
        [],
        this.props
      );
    }
  }

  /**
   * Tries to read an unread message from the queue.
   */
  public async readMessage(log: Logger): Promise<Message> {
    log.trace(this.props, 'Attempting to read message from stream');
    if (this.incrementCounter() === 0) {
      try {
        const msg = await this.tryAndClaimStaleMessage(log);
        return msg;
      } catch (_) {
        /* Logged in function */
      }
    }
    try {
      const m = await this.redis.xreadgroup(
        'GROUP',
        this.consumerGroup,
        this.consumerId,
        'COUNT',
        1,
        'STREAMS',
        this.stream,
        '>'
      );
      if (!m || m.length === 0) throw new Error('No messages in stream');
      if (m.length > 1) throw new Error('Received more messages than expected');
      // Yeah I know... Redis likes nesting arrays
      // This indexing gets the ID for the message, used for the ACK
      const msgId = m[0][1][0][0];

      // This indexing gets the `Value` for the K-V pair of the message
      const msg = m[0][1][0][1][1];
      const message = Message.deserialize(msg, log, msgId);

      return message;
    } catch (error) {
      if (error instanceof Error && error.message.startsWith('No messages'))
        throw error;
      const m = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.STREAM,
        `Failed to read message from stream. ${m}`,
        Category.REDIS,
        log,
        [],
        this.props
      );
    }
  }

  public async acknowledgeMessage(msg: Message, log: Logger): Promise<void> {
    try {
      if (msg.redisMessageId === undefined)
        throw new Error(
          `Can't acknowledge a message if we don't have the ID of that message`
        );
      await this.redis.xack(
        this.stream,
        this.consumerGroup,
        msg.redisMessageId
      );
    } catch (error) {
      const m = error instanceof Error ? error.message : `${error}`;
      throw new OnboardingError(
        MachineError.STREAM,
        `Failed to acknowledge message from stream. ${m}`,
        Category.REDIS,
        log,
        [],
        this.props
      );
    }
  }

  private async tryAndClaimStaleMessage(log: Logger): Promise<Message> {
    log.trace(this.props, 'Trying to claim stale message');
    try {
      const m = await this.redis.xautoclaim(
        this.stream,
        this.consumerGroup,
        this.consumerId,
        STALE_MESSAGE,
        '0-0',
        'COUNT',
        1
      );

      if (!m || m[1].length === 0) throw new Error('No messages in stream');
      if (m[1].length > 1)
        throw new Error('Received more messages than expected');

      const msgId = m[1][0][0];
      const msg = m[1][0][1][1];
      const message = Message.deserialize(msg, log, msgId);

      return message;
    } catch (error) {
      if (error instanceof Error && error.message.startsWith('No messages'))
        throw error;
      log.warn(
        {
          error: error instanceof Error ? error.message : `${error}`,
          ...this.props,
        },
        'Failed to claim stale message from redis'
      );
      throw error;
    }
  }

  private incrementCounter(): number {
    const countToReturn = this.counter;
    if (this.counter === 10) {
      this.counter = 0;
    }
    this.counter += 1;
    return countToReturn;
  }
}

