import { UNREACHABLE } from '../errors';
import { Action } from '../protos/api_pb';

export function actionToString(a: Action): string {
  switch (a) {
    case Action.CREATE:
      return 'CREATE';
    case Action.LINK:
      return 'LINK';
    default:
      throw UNREACHABLE;
  }
}

import { Logger } from 'pino';

import { Props } from '../errors';
import { Action, Entity, OnboardingRequest } from '../protos/api_pb';
import { actionToString } from '../types/action';
import { ExternalUuid } from '../utils';
import { RequestIdTracker } from '../utils/requestId';

import { parseCreateEntity } from './createEntity';
import { parseLinkEntities } from './linkEntities';

export class ValidationWrapper {
  private constructor(
    public readonly data: OnboardingRequest,
    public readonly entity: Entity,
    public readonly entityId: ExternalUuid,
    public logger: Logger
  ) {}

  public static async parseRequest(
    data: OnboardingRequest,
    log: Logger
  ): Promise<ValidationWrapper> {
    let logger = log.child({ currentOperation: 'VALIDATION' });
    const action = data.getAction();
    const requestId = data.getRequestId();
    const tracker = RequestIdTracker.getInstance();
    tracker.addId(requestId, logger);
    const props: Props = {
      action: actionToString(action),
    };
    logger.info(`Attempting to validate ${props.action} ${data.getPayloadCase()}`, {
      requestId
    });

    let entity: Entity;
    let entityId: ExternalUuid;
    switch (action) {
      case Action.CREATE: {
        [entity, entityId, logger] = await parseCreateEntity(
          data,
          logger,
          props
        );
        break;
      }
      case Action.LINK: {
        [entity, entityId, logger] = await parseLinkEntities(
          data,
          logger,
          props
        );
        break;
      }
    }
    const childLogger = logger.child(props);
    return new ValidationWrapper(data, entity, entityId, childLogger);
  }
}

import LRU from 'lru-cache';
import { Logger } from 'pino';
import { validate } from 'uuid';

import { Category, MachineError, OnboardingError } from '../errors';

import { Uuid } from '.';

export class RequestIdTracker {
  private static _instance: RequestIdTracker;

  private cache: LRU<Uuid, null>;

  private constructor() {
    this.cache = new LRU({
      max: 250,
      maxAge: 5 * 60 * 1000,
    });
  }

  public static getInstance(): RequestIdTracker {
    if (this._instance) return this._instance;
    this._instance = new RequestIdTracker();
    return this._instance;
  }

  /**
   * @param {Uuid} id - the request id to add to the tracker
   * @throws if the id is already present in the cache
   * @throws if the id is not a valid uuid
   */
  public addId(id: Uuid, log: Logger): void {
    const isValid = validate(id);
    if (!isValid)
      throw new OnboardingError(
        MachineError.VALIDATION,
        'Request ID is not a valid uuid',
        Category.REQUEST,
        log
      );
    if (this.cache.has(id))
      throw new OnboardingError(
        MachineError.VALIDATION,
        `A request with the id ${id} has already been processed, please generate
        a new id and re-send the request`,
        Category.REQUEST,
        log
      );
    this.cache.set(id, null);
  }
}

import { Logger } from 'pino';

import { BAD_REQUEST, BASE_PATH, Props, tryGetMember } from '../../errors';
import { Entity, OnboardingRequest } from '../../protos/api_pb';
import { ExternalUuid } from '../../utils';

import {
  ValidatedClass,
  ValidatedOrganization,
  ValidatedSchool,
  ValidatedUser,
} from './entities';

export async function parseCreateEntity(
  req: OnboardingRequest,
  log: Logger,
  props: Props
): Promise<[Entity, ExternalUuid, Logger]> {
  const path = [...BASE_PATH];
  const payload = req.getPayloadCase();
  switch (payload) {
    case OnboardingRequest.PayloadCase.ORGANIZATION: {
      path.push('organization');
      const entity = tryGetMember(req.getOrganization(), log, path);
      const validated = await ValidatedOrganization.fromRequest(
        entity,
        log,
        path,
        props
      );
      return [
        Entity.ORGANIZATION,
        validated.org.getExternalUuid(),
        validated.logger,
      ];
    }
    case OnboardingRequest.PayloadCase.SCHOOL: {
      path.push('school');
      const entity = tryGetMember(req.getSchool(), log, path);
      const validated = await ValidatedSchool.fromRequest(
        entity,
        log,
        path,
        props
      );
      return [
        Entity.ORGANIZATION,
        validated.school.getExternalUuid(),
        validated.logger,
      ];
    }
    case OnboardingRequest.PayloadCase.CLASS: {
      path.push('class');
      const entity = tryGetMember(req.getClass(), log, path);
      const validated = await ValidatedClass.fromRequest(
        entity,
        log,
        path,
        props
      );
      return [
        Entity.ORGANIZATION,
        validated.cl.getExternalUuid(),
        validated.logger,
      ];
    }
    case OnboardingRequest.PayloadCase.USER: {
      path.push('user');
      const entity = tryGetMember(req.getUser(), log, path);
      const validated = await ValidatedUser.fromRequest(
        entity,
        log,
        path,
        props
      );
      return [
        Entity.ORGANIZATION,
        validated.user.getExternalUuid(),
        validated.logger,
      ];
    }
    default:
      throw BAD_REQUEST(
        `If action is of type 'CREATE', payload must be one of 'Organization',
        'School', 'Class' or 'User'`,
        path,
        log,
        props
      );
  }
}

