import Joi from 'joi';
import { Logger } from 'pino';

import {
  Category,
  MachineError,
  OnboardingError,
  Props,
} from '../../../errors';
import { Entity, User as PbUser } from '../../../protos';
import { Context } from '../../../utils/context';
import {
  JOI_VALIDATION_SETTINGS,
  VALIDATION_RULES,
} from '../../validationRules';

export class ValidatedUser {
  public static entity = Entity.USER;

  private constructor(public readonly user: PbUser, public logger: Logger) {}

  public static async fromRequest(
    req: PbUser,
    logger: Logger,
    path: string[],
    props: Props
  ): Promise<ValidatedUser> {
    const log = logger.child({
      ...props,
      entity: this.entity,
    });
    const entity = req.toObject();
    ValidatedUser.schemaValidation(entity, log, path, props);
    const newLogger = log.child({
      entityId: entity.externalUuid,
    });
    await ValidatedUser.entityValidation(entity, newLogger);
    return new ValidatedUser(req, newLogger);
  }

  private static schemaValidation(
    entity: PbUser.AsObject,
    log: Logger,
    path: string[],
    props: Props
  ): void {
    const { error } = userSchema.validate(entity, JOI_VALIDATION_SETTINGS);
    if (error)
      throw new OnboardingError(
        MachineError.VALIDATION,
        `Create ${this.entity} request has failed validation`,
        Category.REQUEST,
        log,
        path,
        props,
        error.details.map((e) => e.message)
      );
    // This is to validate the custom logic around requiring either
    // - USERNAME + PHONE
    // - USERNAME + EMAIL
    // (or both). This can't be handled by JOI
    const { email, phone } = entity;
    const phoneRegex = new RegExp(VALIDATION_RULES.PHONE_REGEX);
    const emailRegex = new RegExp(VALIDATION_RULES.EMAIL_REGEX);
    const phoneIsValid = phoneRegex.exec(phone);
    const emailIsValid = emailRegex.exec(email);
    if (phoneIsValid === null && emailIsValid === null) {
      path.push('phone');
      throw new OnboardingError(
        MachineError.VALIDATION,
        `${this.entity} failed validation`,
        Category.REQUEST,
        log,
        [
          'Phone and Email are invalid, at least one of the two must be valid',
          `Must provide a combination of either 'PHONE' + 'USERNAME' or 'EMAIL' + 'USERNAME'`,
        ]
      );
    }
  }

  private static async entityValidation(
    e: PbUser.AsObject,
    log: Logger
  ): Promise<void> {
    const ctx = Context.getInstance();
    await ctx.organizationIdIsValid(e.externalOrganizationUuid, log);
    await ctx.schoolIdIsValid(e.externalSchoolUuid, log);
  }
}

export const userSchema = Joi.object({
  externalUuid: Joi.string().guid({ version: ['uuidv4'] }),

  externalOrganizationUuid: Joi.string()
    .guid({ version: ['uuidv4'] })
    .required(),

  externalSchoolUuid: Joi.string()
    .guid({ version: ['uuidv4'] })
    .required(),

  givenName: Joi.string()
    .min(VALIDATION_RULES.USER_NAME_MIN_LENGTH)
    .max(VALIDATION_RULES.USER_NAME_MAX_LENGTH)
    .regex(VALIDATION_RULES.ALPHANUMERIC)
    .required(),

  familyName: Joi.string()
    .min(VALIDATION_RULES.USER_NAME_MIN_LENGTH)
    .max(VALIDATION_RULES.USER_NAME_MAX_LENGTH)
    .regex(VALIDATION_RULES.ALPHANUMERIC)
    .required(),

  username: Joi.string().required(),

  // Due to niche rules, need to validate in ValidationWrapper.validate
  email: Joi.any(),
  phone: Joi.any(),

  dateOfBirth: Joi.date().max('now'),

  // 0 = Male, 1 = Female
  gender: Joi.number().min(0).max(1).required(),
});

import { Logger } from 'pino';

import { Entity, ExternalUuid, Uuid } from '../..';
import {
  BAD_REQUEST,
  BASE_PATH,
  Category,
  MachineError,
  OnboardingError,
  Props,
  tryGetMember,
} from '../errors';
import {
  EntitiesToLink,
  LinkEntities,
  OnboardingRequest,
  Entity as PbEntity,
} from '../protos/api_pb';
import { Context } from '../utils/context';

export type LinkEntitiesRequest = {
  primary: LinkEntity;
  secondary: LinkEntity;
};

export type LinkEntity = {
  entity: Entity;
  identifier: string | Uuid;
};

export async function parseLinkEntities(
  req: OnboardingRequest,
  log: Logger,
  props: Props
): Promise<[PbEntity, ExternalUuid, Logger]> {
  const path = [...BASE_PATH];
  const payload = req.getPayloadCase();
  if (payload !== OnboardingRequest.PayloadCase.LINK_ENTITIES)
    throw BAD_REQUEST(
      `If Action is of type 'LINK', payload must be 'LinkEntites'`,
      path,
      log,
      props
    );

  path.push('linkEntities');
  const body = tryGetMember(req.getLinkEntities(), log, path);
  const { pbEntity, targetEntity, targetEntityId } = await parseTargetEntity(
    body,
    log,
    path
  );

  props['targetEntity'] = targetEntity;
  props['targetEntityId'] = targetEntityId;

  const childPath = [...path, 'entities'];
  const { childEntites, childIds } = await parseEntitiesToLink(
    body.getExternalOrganizationUuid(),
    tryGetMember(body.getEntities(), log, childPath),
    log,
    childPath
  );

  props['entityToLink'] = childEntites;
  props['entityIdsToLink'] = childIds;

  return [pbEntity, targetEntityId, log.child(props)];
}

async function parseTargetEntity(
  body: LinkEntities,
  log: Logger,
  path: string[]
): Promise<{
  pbEntity: PbEntity;
  targetEntity: Entity;
  targetEntityId: ExternalUuid;
}> {
  let pbEntity: PbEntity;
  let targetEntity: Entity;
  let targetEntityId: ExternalUuid;
  const ctx = Context.getInstance();
  switch (body.getTargetCase()) {
    case LinkEntities.TargetCase.ORGANIZATION: {
      pbEntity = PbEntity.ORGANIZATION;
      targetEntity = Entity.ORGANIZATION;
      targetEntityId = tryGetMember(body.getOrganization(), log, [
        ...path,
        'organization',
      ]).getExternalUuid();
      await ctx.organizationIdIsValid(targetEntityId, log);
      if (body.getExternalOrganizationUuid() !== targetEntity)
        throw new OnboardingError(
          MachineError.REQUEST,
          `Found that the Organization ID provided and the entity identifier provided did not match despite the entity being set to 'ORGANIZATION'`,
          Category.REQUEST,
          log,
          path
        );
      break;
    }
    case LinkEntities.TargetCase.SCHOOL: {
      pbEntity = PbEntity.SCHOOL;
      targetEntity = Entity.SCHOOL;
      targetEntityId = tryGetMember(body.getSchool(), log, [
        ...path,
        'school',
      ]).getExternalUuid();
      await ctx.schoolIdIsValid(targetEntityId, log);
      break;
    }
    case LinkEntities.TargetCase.CLASS: {
      pbEntity = PbEntity.CLASS;
      targetEntity = Entity.CLASS;
      targetEntityId = tryGetMember(body.getClass(), log, [
        ...path,
        'class',
      ]).getExternalUuid();
      await ctx.classIdIsValid(targetEntityId, log);
      break;
    }
    case LinkEntities.TargetCase.USER: {
      pbEntity = PbEntity.USER;
      targetEntity = Entity.USER;
      targetEntityId = tryGetMember(body.getUser(), log, [
        ...path,
        'user',
      ]).getExternalUuid();
      await ctx.userIdIsValid(targetEntityId, log);
      break;
    }
    default:
      throw BAD_REQUEST(
        `A request to LinkEntites must include either an 'ORGANIZATION',
        'SCHOOL', 'CLASS' or 'USER'`,
        path,
        log
      );
  }
  return { pbEntity, targetEntity, targetEntityId };
}

async function parseEntitiesToLink(
  orgId: ExternalUuid,
  body: EntitiesToLink,
  log: Logger,
  path: string[]
): Promise<{
  childEntites: Entity;
  childIds: ExternalUuid[];
}> {
  const { externalEntityIdentifiersList, entity } = body.toObject();
  const ctx = Context.getInstance();
  let e: Entity;
  switch (entity) {
    case PbEntity.ORGANIZATION: {
      // @TODO - Create Find Many Queries
      for (const id of externalEntityIdentifiersList) {
        await ctx.organizationIdIsValid(id, log);
      }
      e = Entity.ORGANIZATION;
      break;
    }
    case PbEntity.SCHOOL: {
      // @TODO - Create Find Many Queries
      for (const id of externalEntityIdentifiersList) {
        await ctx.schoolIdIsValid(id, log);
      }
      e = Entity.SCHOOL;
      break;
    }
    case PbEntity.CLASS: {
      // @TODO - Create Find Many Queries
      for (const id of externalEntityIdentifiersList) {
        await ctx.classIdIsValid(id, log);
      }
      e = Entity.CLASS;
      break;
    }
    case PbEntity.USER: {
      // @TODO - Create Find Many Queries
      for (const id of externalEntityIdentifiersList) {
        await ctx.userIdIsValid(id, log);
      }
      e = Entity.USER;
      break;
    }
    case PbEntity.PROGRAM: {
      await ctx.programsAreValid(externalEntityIdentifiersList, orgId, log);
      e = Entity.PROGRAM;
      break;
    }
    case PbEntity.ROLE: {
      await ctx.rolesAreValid(externalEntityIdentifiersList, orgId, log);
      e = Entity.ROLE;
      break;
    }
    default:
      throw BAD_REQUEST(
        `'ENTITY' must be set when trying to link entities`,
        path,
        log
      );
  }
  return {
    childEntites: e,
    childIds: externalEntityIdentifiersList,
  };
}

